mapper.select:
select count(*) from blog_user
where 1=1


    email = #{email,jdbcType=VARCHAR}


    or username = #{username,jdbcType=VARCHAR}


    or phone_number = #{phoneNumber,jdbcType=VARCHAR}

SELECT bu.user_uuid, bu.username, bu.full_name, bu.email, bu.password, bu.birthday, bu.phone_number, bu.gender, bu.user_status, bu.is_edit, bu.is_delete, br.role_name, br.role_right, br.role_status, bar.module_code, bar.access_right, bm.sort FROM blog_user bu inner join blog_role br on br.role_id = bu.role_id inner join blog_access_right bar on bar.role_id = bu.role_id inner join blog_module bm on bm.module_code = bar.module_code WHERE 1=1
(bu.user_uuid = #{userUuid,jdbcType=VARCHAR} and bu.username = #{username,jdbcType=VARCHAR})
user_uuid = #{userUuid,jdbcType=VARCHAR}
username = #{username,jdbcType=VARCHAR}
-- AND bu.user_status = 'Active' -- AND br.role_status = '1' order by bm.sort ASC;
